def connectionFunc(srclocs,dstlocs,sigma,connType):
import math import random import sys
# Corrected normterm (2015/01/16): normterm = 1/(math.pow(sigma,2)*2*math.pi)
i = 0 out = [] for srcloc in srclocs: j = 0
S1 = round( 25 + random.gauss(0, 1) ) if connType==0: if srcloc[0]==0: S0 = round( 11 + random.gauss(0, 1) ) else: S0 = round( 36 + random.gauss(0, 1) ) else: if srcloc[0]==0: S0 = round( 36 + random.gauss(0, 1) ) else: S0 = round( 11 + random.gauss(0, 1) )
for dstloc in dstlocs: dist = math.sqrt(math.pow((S0 - dstloc[0]),2) + math.pow((S1 - dstloc[1]),2) + math.pow((srcloc[2] - dstloc[2]),2))
gauss = normterm*math.exp(-0.5*math.pow(dist/sigma,2))
if gauss > 0.001: #sys.stdout.write('gauss>0.0001: i={0} gauss={1}'.format( i, gauss)) conn = (i,j,0,gauss) out.append(conn)
j = j + 1 i = i + 1 #sys.stdout.write('out length: %d' % len(out)) return out

